INSERT INTO `producttype_level2` (`id`, `name`, `name_en`, `meta_title`, `meta_description`, `header_description`, `id_producttype_level1`, `ordinal_number`, `status`) VALUES
(1, 'โต๊ะรีดผ้าและอุปกรณ์เสริม', '', 'ผลิตภัณฑ์ใช้ในบ้าน ของใช้ในบ้าน ของตกแต่งบ้าน', 'สินค้าใช้ในบ้าน ของใช้ในบ้านพักและของตกแต่งบ้าน ', '<h2><strong>โต๊ะรีดผ้า : Ironing Table</strong></h2>ผลิตด้วยวัสดุคุณภาพมาตรฐานสากล ขาเหล็กหนาทนทานไม่โยกคลอน รับแรงกดได้เป็นอย่างดี หุ้มด้วยผ้าสะท้อนความร้อน ไม่ซับน้ำ รีดผ้าได้เรียบสนิททั้งสองด้าน<br /> <br /> <h2><strong>ผ้ารองรีด : Reflexive Heat Fabric</strong></h2>ผ้ารองรีดสะท้อนความร้อน ด้วยผ้าคุณภาพ Cotton 100% เคลือบสาร Aluminium Powder ทนความร้อน รีดผ้าได้เรียบ 2ด้าน ไม่เปียกชื้น ประหยัดไฟฟ้า เหมาะสมกับเตารีดธรรมดาและไอน้ำ ใช้คู่กับโต๊ะรีดผ้าในโรงแรม ร้านซักรีดและบ้านพักทั่วไป<br /> <br /> <h2><strong>ที่แขวนโต๊ะรีดผ้า ติดผนัง : Iron Organizer</strong></h2>แขวนโต๊ะรีดผ้าและวางเตารีดร้อนหลังใช้งาน ให้ความสะดวกปลอดภัยในการเก็บหรือหยิบใช้ทำจากวัสดุเคลือบสารทนความร้อน เหมาะสำหรับ โรงแรม รีสอร์ท คอนโด เซอร์วิสอพารท์เม้นท์ สถานประกอบการเสื้อผ้าและใช้ตาม บ้านพักทั่วไป', 1, 0, 'Y'),
(2, 'ไม้แขวนเสื้อ', '', '', '', '<h2><strong>ไม้แขวนเสื้อ : Hanger</strong></h2>ไม้แขวนเสื้อ ไม้แขวนผ้า สำหรับใช้ในโรงแรม รีสอร์ท เซอร์วิสอพาร์ทเมนท์ กิจการเสื้อผ้าแฟชั่น ชั้นนำในประเทศและต่างประเทศ ทำจากไม้ Lotus อย่างดี งานเข้าลิ้น ไม่มีรอยต่อซ้าย-ขวา สวยงามด้วยฝีมือประณีต ความหนาของเนื้อไม้ 14มิล ช่วยให้มีอายุการใช้งานคงทน มีให้เลือกหลายสี ไม้แขวนทุกแบบมีให้เลือกทั้งที่เป็นชนิดห่วงตะขอ หรือ เป็นห่วงหัวเข็ม วงแหวนกันสูญหายมีหลายแบบ รับสั่งทำ ตามขนาดพร้อมปั้ม Logo รวมทั้งไม้แขวนผ้าซาตินหลากสี และไม้แขวนกำมะหยี่อย่งดีไม่หักงอ ยืดหยุ่น ช่วยประหยัดพื้นที่ในการแขวนในตู้เสื้อผ้า', 1, 0, 'Y'),
(3, 'กระจกแต่งหน้า', '', '', '', '<h2><strong>กระจกส่องหน้า : Magnifying Mirror</strong></h2>กระจกส่องหน้าติดผนัง กระจกขยาย กระจกติดผนัง กระจกติดผนังมีไฟส่อง รูปทรงทันสมัย สวยงาม คงทน มีทั้งแบบตั้งโต๊ะ แบบที่ยึดติดผนัง มีขนาดให้เลือกใช้ตามความเหมาะสม เก็บพับ หมุนได้ 360 ํ ทุกมุมมองตัวกระจกฉาบเนียน ขยาย 3เท่า 5เท่า ผิววัสดุชุบโครเมียมอย่างดี รุ่นพิเศษเปิดแสงไฟจากหลอด LED เหมาะสำหรับใช้ในโรงแรม รีสอร์ท เซอร์วิสอพาร์ทเม้นท์', 1, 0, 'Y'),
(4, 'ตู้เซฟและอุปกรณ์นิรภัย', '', '', '', '<h2><strong>ตู้เซฟโรงแรม : Hotel Safe Box</strong></h2>ตู้เซฟเล็กโรงแรม ตู้เซฟในห้องพัก ตู้เชฟระบบอีเล็กทรอนิกส์ เพื่อเก็บของมีค่าและโน๊ตบุ๊คได้อย่างปลอดภัย เพียงกดปุ่มตัวเลข เพื่อตั้งรหัสเปิด-ปิดส่วนตัว รุ่นพิเศษใช้การ์ดแบบสัมผัสเปิด-ปิด พร้อมบันทึกการใช้งาน รูปทรงหน้าปัดทันสมัย จอแสดงตัวเลขดิจิตอล ลืมรหัสสามารถเปิดได้ด้วยรหัสตัวเครื่อง (ตั้งค่ารหัส 6 หลักได้เอง) หรือกุญแจ เหมาะสำหรับติดตั้งในห้องพักโรงแรม รีสอร์ท และเซอร์วิสอพาร์ทเม้นท์ และสำนักงานทั่วไป', 1, 0, 'Y'),
